mapper.select:
select orderinfo.order_id as orderId, orderinfo.user_id as userId, orderinfo.goods_id as goodId, orderinfo.goods_name as goodName, FROM_UNIXTIME(orderinfo.order_time,'%Y-%m-%d %H:%i:%s') as orderTime, goodsinfo.goods_photo as goodImgSrc, goodsinfo.price as goodPrice, orderinfo.goods_num as goodNumber, orderinfo.order_state as orderStatus, orderinfo.pay as orderAmount, orderinfo.real_pay as realPay, FROM_UNIXTIME(orderinfo.pay_time,'%Y-%m-%d %H:%i:%s') as payTime, orderinfo.pay_way as payWay from zx_orderinfo orderinfo left join zx_goodsinfo goodsinfo on orderinfo.goods_id = goodsinfo.goods_id where 1 = 1
and orderinfo.user_id = #{userId}
LIMIT #{pageSize} offset #{pageOffset}

select
count(1)
from zx_orderinfo orderinfo
where 1 = 1

    and orderinfo.user_id = #{userId}


    LIMIT #{pageSize} offset #{pageOffset}

select saleway.saleway_id as salewayId, saleway.saleway_type as salewayType, saleway.sub_type as subType, saleway.saleway_des as salewayDes, saleway.saleway_scale as salewayScale, saleway.judge_price as judgePrice, saleway.saleway_use from zx_saleway saleway where 1 = 1
and saleway.saleway_type = #{salewayType}
and saleway.sub_type = #{subType}
saleway.saleway_use = #{salewayUse}

mapper.update:
update zx_tickets ticket

    set ticket.tickets_num = #{item.ticketsNum}
    where
    ticket.tickets_id = #{item.ticketsId}

mapper.select:
select saleway.saleway_id as salewayId, saleway.saleway_type as salewayType, saleway.sub_type as subType, saleway.saleway_des as salewayDes, saleway.saleway_scale as salewayScale, saleway.judge_price as judgePrice, saleway.saleway_use as salewayUse from zx_saleway saleway where 1 = 1
and saleway.saleway_id = #{salaryId}
and saleway.saleway_type = #{salaryType}
and saleway.sub_type = #{subType}
